import streamlit as stimport numpy as npimport sysimport osROOT_DIR = os.path.dirname(os.path.dirname(os.path.abspath(__file__)))sys.path.append(ROOT_DIR)from src.inference import predict_revenue, predict_hitst.title("ðŸŽ¬ Movie Revenue & Hit Predictor")st.write("Enter the caracteristic of the film.")# Inputs utilisateurbudget = st.number_input("Budget du film ($)", min_value=1000, value=5000000)popularity = st.number_input("Popularity", min_value=0.0, value=10.0)vote_average = st.number_input("vote_average", min_value=0.0, max_value=10.0, value=7.5)runtime = st.number_input("runtime (min)", min_value=30, value=120)if st.button("Predict"):    log_budget = np.log(budget)    revenue_pred = predict_revenue(log_budget, popularity, vote_average, runtime)    hit_proba = predict_hit(log_budget, popularity, vote_average, runtime)    st.subheader("ðŸ“ˆ Results")    st.write(f"**predict revenu  :** ${revenue_pred:,.0f}")    st.write(f"**Probability to be a hit :** {hit_proba*100:.1f}%")