import joblibimport numpy as npfrom pathlib import PathMODELS_DIR = Path("models")revenue_model = joblib.load(MODELS_DIR / "revenue_model.joblib")hit_model = joblib.load(MODELS_DIR / "hit_model.joblib")def predict_revenue(log_budget, popularity, vote_average, runtime):    """    Prédit le revenu d'un film à partir des features.    Retourne le revenu en dollars (échelle normale).    """    X = np.array([[log_budget, popularity, vote_average, runtime]])    log_rev_pred = revenue_model.predict(X)[0]    revenue_pred = np.exp(log_rev_pred)  # revenir à l'échelle normale    return revenue_preddef predict_hit(log_budget, popularity, vote_average, runtime):    """    Prédit la probabilité qu'un film soit un hit (1 = hit).    """    X = np.array([[log_budget, popularity, vote_average, runtime]])    proba = hit_model.predict_proba(X)[0, 1]    return proba 